function drawstamen(){stamens.length<maxstamen&&stamens.push(new Stamen);for(let p of stamens){if(p.LSlimit/2<p.LS){stroke(color(p.hu,100,max(70,p.LS))),strokeWeight(bx/16);for(let i=0;i<10;i++)line(p.maxpos.x,p.maxpos.y,p.maxpos.x+nz(1)*LLR/7,p.maxpos.y+nz(1)*LLR/7)}maxpos=p.pos.copy();let vel=p.vel.copy();for(let i=-1;i<=min(p.LS,p.LSlimit);i+=.2){vel.add(p.acc),vel.setMag(layers.length>4?LLR/50:LLR/90);let pos=maxpos.copy().add(vel);stroke(color(p.hu,100,3*p.LS,i/100)),strokeWeight(bx/8),line(maxpos.x,maxpos.y,pos.x,pos.y),maxpos=pos.copy()}p.maxpos=maxpos.copy(),p.LS+=.2,p.acc.mult(1.001)}stamens=stamens.filter(p=>!(p.LS>p.LSlimit&&nzr()>.85))}class Stamen{constructor(){this.pos=createVector(home.x-nz(1)*LLR/2,home.y-nz(1)*LLR/2),this.acc=createVector(0,.001*bx),this.vel=this.pos.copy();let h=p5.Vector.sub(this.pos,home);this.vel.add(h),this.vel.setMag(bx),this.LS=-1,this.hu=hu+160*nzr(),this.LSlimit=random(10,20),this.maxpos=this.pos.copy()}}